public static void readTxt(){

  try {
	              String encoding = "GBK"; // 字符编码(可解决中文乱码问题 )
	              File file = new File(FILENAME);
	              if (file.isFile() &amp;&amp; file.exists()) {
	                  InputStreamReader read = new InputStreamReader(
	                		  new FileInputStream(file), encoding);
	                  BufferedReader bufferedReader = new BufferedReader(read);
	                  String lineTXT = null;
	                  while ((lineTXT = bufferedReader.readLine()) != null) {
	                       System.out.println(lineTXT.toString().trim());
	                  }
	                  read.close();
	              }else{
	                  System.out.println("找不到指定的文件！");
	           }
	         } catch (Exception e) {
	             System.out.println("读取文件内容操作出错");
	              e.printStackTrace();
	           }

}

/**
 * 写入文件用RandomAccessFile
 * 写入最后一行，主要是处理json原来结尾处的];
 */
public static void writeLastLine(String str){
	if(str==""){System.out.println("str为空");}

	try {
		RandomAccessFile raf = new RandomAccessFile(FILENAME, "rw");
		if(raf.length()==0){
			raf.writeBytes("["+str+"]");
			raf.close();}
		else{
			raf.seek(raf.length()-1);
			//String str=",\r\n{\"time\":\"2013-03-1520:39:08\",\"f1\":\"689\",\"f2\":\"5.742885\",\"f3\":\"13663.0564\",\"f4\":\"277\",\"f5\":\"2.339423\",\"f6\":\"5743.1049\",\"f7\":\"270\",\"f8\":\"2.258743\",\"f9\":\"5684.1790\",\"f10\":\"106\",\"f11\":\"0.826265\",\"f12\":\"2181.2984\",\"f13\":\"145\",\"f14\":\"1.331051\",\"f15\":\"3545.9744\",\"f16\":\"72\",\"f17\":\"0.689085\",\"f18\":\"1779.3651\"}]";
			raf.writeBytes(",\r\n"+str+"]");
			raf.close();
		}

	} catch (Exception e) {
		// TODO Auto-generated catch block
		e.printStackTrace();
	}

}
/**
 * 写入文件用FileWriter
 *
 */
public static void writeTxt(String filename,String outString){

File myFile = new File(filename);
	try {
		if (!myFile.exists()) {
			myFile.createNewFile();
		}

		FileWriter resultFileWriter = new FileWriter(myFile, true);
		PrintWriter myNewFilePrintWriter = new PrintWriter(
				resultFileWriter);
		myNewFilePrintWriter.println(outString);
		resultFileWriter.close();
		System.out.println("写入");
	} catch (IOException e) {
		// TODO Auto-generated catch block
		e.printStackTrace();
	}

}
