//开启Three.js渲染器
var renderer;//声明全局变量（对象）
var width;
var height
function initThree() {
width = document.getElementById('canvas3d').clientWidth;//获取画布「canvas3d」的宽
height = document.getElementById('canvas3d').clientHeight;//获取画布「canvas3d」的高
renderer=new THREE.WebGLRenderer({antialias:true});//生成渲染器对象（属性：抗锯齿效果为设置有效）
//renderer=new THREE.CanvasRenderer();
renderer.setSize(width, height );//指定渲染器的高宽（和画布框大小一致）
document.getElementById('canvas3d').appendChild(renderer.domElement);//追加 【canvas】 元素到 【canvas3d】 元素中。
renderer.setClearColorHex(0xFFFFFF, 1.0);//设置canvas背景色(clearColor)
}
//摄像机
var camera;
function initCamera() {
camera = new THREE.PerspectiveCamera( 45, width / height , 1 , 5000 );//设置透视投影的相机,默认情况下相机的上方向为Y轴，右方向为X轴，沿着Z轴朝里（视野角：fov 纵横比：aspect 相机离视体积最近的距离：near 相机离视体积最远的距离：far）
camera.position.x = 0;//设置相机的位置坐标
camera.position.y = 100;//设置相机的位置坐标
camera.position.z = 200;//设置相机的位置坐标
camera.up.x = 0;//设置相机的上为「x」轴方向
camera.up.y = 1;//设置相机的上为「y」轴方向
camera.up.z = 0;//设置相机的上为「z」轴方向
camera.lookAt( {x:0, y:0, z:0 } );//设置视野的中心坐标
}
//设置场景
var scene;
function initScene() {
scene = new THREE.Scene();
}

//设置光源
/*
* 点光源，比如灯泡。
* 聚光灯比如舞台灯光
* 平行光，比如太阳光，从无限远的地方
*
* */
var light;
function initLight(){
light = new THREE.DirectionalLight(0xffffff,1.0,0); //三个参数，颜色，密度，距离0表示无限远
light.position.set(100,100,200);//设置光源位置
//将光源加入场景
scene.add(light);// 追加光源到场景
}
//设置物体
/*
* 物体需要材质，才能看见。
* */
var line;
function initObject(){
var geometry = new THREE.Geometry();
var material = new THREE.LineBasicMaterial({vertexColors:THREE.VertexColors});//材质 后面的参数表示顶点颜色由我们传递进去
var color1 = new THREE.Color(0x00ffff);
var color2 = new THREE.Color(0x00ffff);
var color3 = new THREE.Color(0x00ffff);
var color4 = new THREE.Color(0x00ffff);
geometry.vertices.push(new THREE.Vector3(0,0,0));
geometry.vertices.push(new THREE.Vector3(0,0,100)); //定义一个点
geometry.vertices.push(new THREE.Vector3(-100,0,-100));
geometry.vertices.push(new THREE.Vector3(0,0,0));
geometry.colors.push(color1,color2,color3,color4);//传进去的颜色会渐变
//line = new THREE.Line(geometry,material,THREE.LinePieces);//图形，材质,画线方式
line = new THREE.Line(geometry,material,THREE.LineStrip);//图形，材质,画线方式
/*GL_POINTS 只画两个点
GL_LINES，还有GL_LINE_STRIP，GL_LINE_LOOP，GL_TRIANGLES，GL_TRIANGLE_STRIP，GL_TRIANGLE_FAN
*详情可以http://www.cnblogs.com/FredCong/archive/2012/10/13/2722893.html 搜索gl_points
* */
scene.add(line);
}

function threeStart(){
initThree();
initCamera();
initScene();
initLight();
initObject();
renderer.clear();
renderer.render(scene, camera);//渲染，场景，相机

}

