	/**
	 * 读取文件最后一行
	 * @param file  文件名
	 * @param charset 编码
	 * @return
	 * @throws IOException
	 */
	public static String readLastLine(String filename, String charset) throws IOException {
		File file = new File(filename);
		  if (!file.exists() || file.isDirectory() || !file.canRead()) {
		    return "";
		  }
		  RandomAccessFile raf = null;
		  try {
		    raf = new RandomAccessFile(file, "r");
		    long len = raf.length();   //获取长度
		    if (len == 0L) {
		      return "";
		    } else {
		      long pos = len - 1;
		      while (pos > 0) {   //不停的移动指针直到遇到换行符
		        pos--;
		        raf.seek(pos);
		        if (raf.readByte() == '\n') {
		          break;
		        }
		      }
		      if (pos == 0) {
		        raf.seek(0);
		      }
		      byte[] bytes = new byte[(int) (len - pos)];   //最后一排的长度
		      raf.read(bytes);   //将最多 b.length 个数据字节从此文件读入 byte 数组
		      if (charset == null) {
		        return new String(bytes);
		      } else {
		        return new String(bytes, charset);
		      }
		    }
		  } catch (FileNotFoundException e) {
		  } finally {
		    if (raf != null) {
		      try {
		        raf.close();
		      } catch (Exception e2) {
		      }
		    }
		  }
		  return null;
		}
	/**
	 * 写入文件用RandomAccessFile
	 * 写入最后一行，主要是处理json原来结尾处的];
	 */
	public static void writeLastLine(String str){
		if(str==""){System.out.println("str为空");}

		try {
			RandomAccessFile raf = new RandomAccessFile(FILENAME, "rw");
			if(raf.length()==0){
				raf.writeBytes("["+str+"]");
				raf.close();}
			else{
				raf.seek(raf.length()-1);
				//String str=",\r\n{\"time\":\"2013-03-1520:39:08\",\"f1\":\"689\",\"f2\":\"5.742885\",\"f3\":\"13663.0564\",\"f4\":\"277\",\"f5\":\"2.339423\",\"f6\":\"5743.1049\",\"f7\":\"270\",\"f8\":\"2.258743\",\"f9\":\"5684.1790\",\"f10\":\"106\",\"f11\":\"0.826265\",\"f12\":\"2181.2984\",\"f13\":\"145\",\"f14\":\"1.331051\",\"f15\":\"3545.9744\",\"f16\":\"72\",\"f17\":\"0.689085\",\"f18\":\"1779.3651\"}]";
				raf.writeBytes(",\r\n"+str+"]");
				raf.close();
			}

		} catch (Exception e) {
			// TODO Auto-generated catch block
			e.printStackTrace();
		}

	}
	public static void main(String[] args) {
		JFrame frame = new JiaoyiJson();
		frame.setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);//不加这句关闭后总是有个javaw进程。
