function initialize() {

var newark = new google.maps.LatLng(40.740, -74.18);
var imageBounds = new google.maps.LatLngBounds(
new google.maps.LatLng(40.712216,-74.22655), //左下坐标
new google.maps.LatLng(40.773941,-74.12544));//右上坐标  知道这两个坐标就能判断范围

var mapOptions = {
zoom: 13,
center: newark,
mapTypeId: google.maps.MapTypeId.ROADMAP
}
var map = new google.maps.Map(document.getElementById('map-canvas'), mapOptions);

var marker = new google.maps.Marker({
position: new google.maps.LatLng(40.712216,-74.22655),
map: map,
draggable : true,
});
var marker = new google.maps.Marker({
position: new google.maps.LatLng(40.773941,-74.12544),
map: map,
draggable : true,
});

var oldmap = <span style="color: #ff6600;">new google.maps.GroundOverlay</span>(
//'https://www.lib.utexas.edu/maps/historical/newark_nj_1922.jpg',
'./1.jpg',

imageBounds);
oldmap.setMap(map);
}

google.maps.event.addDomListener(window, 'load', initialize);

