   String sCkValue = new String(request.getParameter("CkValue").getBytes("ISO8859_1")).trim();
   System.out.println("ckValue="+java.net.URLEncoder.encode(sCkValue,"UTF-8"));
   Cookie ckNew = new Cookie(sCkName,null);
   ckNew.setMaxAge(0); ckNew.setPath("/");
   response.addCookie(ckNew);
   Cookie ckNew1 = new Cookie(sCkName,java.net.URLEncoder.encode(sCkValue,"UTF-8"));
   ckNew1.setMaxAge(365*24*60*60);
   response.addCookie(ckNew1);
   } catch(Exception ex) { }</pre>
