<span style="color: #000080;">   //获取元素属性,传入数组对象
   function getCloumnArr(dataList,columName){
      var cloumnArr=[];
      for(var i = 0; i &lt; dataList.length; i++){
         for(var prop in dataList[i]) {   //遍历元素属性,查找
            if(prop==columName){
               cloumnArr.push(dataList[i][columName]);//这里传入字符串  javascript 可以通过dataList[i]["属性值访问到属性"]
            }
         }
      }
      //console.log(cloumnArr);
      return cloumnArr;
   }
