var colorHex = function(value) {
var color = value.replace(/[^\d,]/g,'').replace(/(\d+)/g, function(s, s1) {
var v = parseInt(s1).toString(16);
return v.length == 1 ? '0'+v : v;
});
return '#'+color.replace(/,/g, '').toUpperCase();
};
