    function addMarker(mName,mLat,mLng){
        // Create the placemark.
        var placemark = ge.createPlacemark('');
        placemark.setName(mName);

        // Set the placemark's location.
        var point = ge.createPoint('');
        point.setLatitude(mLat);
        point.setLongitude(mLng);
        placemark.setGeometry(point);

        // Add the placemark to Earth.
        ge.getFeatures().appendChild(placemark);
    }
//添加一个placemark（图片）
    function addIconMarker(mName,lttd,lgtd){
        var placemark ;
        placemark = ge.createPlacemark("");

        var icon = ge.createIcon('');
        icon.setHref('图片地址');           //图片地址需要是网络地址http://

        var style = ge.createStyle('');
        style.getIconStyle().setIcon(icon);
        style.getIconStyle().setScale(0.6); //原尺寸*0.6
        style.getLabelStyle().getColor().set('ff0000ff'); //字体颜色 前面的ff指透明度范围(00-ff)，后面的6位表示颜色   api地址 https://developers.google.com/earth/documentation/reference/interface_kml_color
        style.getLabelStyle().setScale(0.8);
        placemark.setStyleSelector(style);

        placemark.setName(value);
        var point = ge.createPoint('');
        point.setLatitude(lttd);
        point.setLongitude(lgtd);
        placemark.setGeometry(point);
        placemark.setVisibility(true);
        // Add the placemark to Earth.
        ge.getFeatures().appendChild(placemark);
    }
//划线
    function drawLineString(){
        var lineStringPlacemark = ge.createPlacemark('');

        // Create the LineString
        var lineString = ge.createLineString('');
        // Add LineString points
        lineString.getCoordinates().pushLatLngAlt(37.43042,-122.091064,0);
        lineString.getCoordinates().pushLatLngAlt(37.43042,-122.080078,0);
        lineString.getCoordinates().pushLatLngAlt(37.419434,-122.080078,0);
        lineString.getCoordinates().pushLatLngAlt(37.419434,-122.091064,0);
        lineString.getCoordinates().pushLatLngAlt(37.43042,-122.091064,0);
        lineStringPlacemark.setGeometry(lineString);

        // Add the feature to Earth
        ge.getFeatures().appendChild(lineStringPlacemark);
    }

//添加一张图片
    function addPic(){
        // Create the GroundOverlay
        var groundOverlay = ge.createGroundOverlay('');

        // Specify the image path and assign it to the GroundOverlay
        var icon = ge.createIcon('');
        icon.setHref("http://localhost/googleEarth/image.JPEG"); //好像必须是网络地址，用 类似  ./images.jpge 无反应
        groundOverlay.setIcon(icon);

        // Specify the geographic location
        var latLonBox = ge.createLatLonBox('');
        latLonBox.setBox(37.430419921875, 37.41943359375, -122.080078125, -122.091064453125, 0);
        groundOverlay.setLatLonBox(latLonBox);

        // Add the GroundOverlay to Earth
        ge.getFeatures().appendChild(groundOverlay);
    }

    /**
    *
    * @param speed 飞行速度
    * @param lat 经度
    * @param lon 维度
    * @param tilt 角度
    * @param range 放大级别
    * Demo:flyTo(0.5, 30.8313, 111.36658, 45, 25000);
    */
    function flyTo(speed,lat,lon,tilt,range){
        ge.getOptions().setFlyToSpeed(speed);
        var lookAt = ge.getView().copyAsLookAt(ge.ALTITUDE_RELATIVE_TO_GROUND);
        lookAt.setTilt(tilt);
        lookAt.setLongitude(lon);
        lookAt.setLatitude(lat);
        lookAt.setRange(range);
        ge.getView().setAbstractView(lookAt);
    }
//删除一个对象
ge.getFeatures().removeChild(对象)

//隐藏id中包含指定字符的placemark
   /*
   ele: 名字所包含的要素 标注名类似 ******_PP，可以传入数组["xx","yy","zz"]
   vis: true|false  显示|隐藏
   使用setAutoStationVisByElement("PP",true)、setAutoStationVisByElement(["PP","PP1","PP2"],true)。
   */
   function setAutoStationVisByElement(ele, vis) {
        if(ele instanceof Array && (ele.length>0)){//如果参数是数组["PP","VT"]
            var placemarks = ge.getElementsByType('KmlPlacemark');
            for (var i = 0; i < placemarks.getLength(); ++i) {
               var placemark = placemarks.item(i);
                for(var j in ele){
                    if( (placemark.getId()+"").indexOf(ele[j]) > -1) {
                      placemark.setVisibility(vis);
                    }
                }
            }
        } else {//如果是参数
            var placemarks = ge.getElementsByType('KmlPlacemark');
            for (var i = 0; i < placemarks.getLength(); ++i) {
               var placemark = placemarks.item(i);
              // console.log( placemark.getId());
               if( (placemark.getId()+"").indexOf(ele) > -1) {
                  placemark.setVisibility(vis);
               }
            }
        }
    }

